G=(1.5,1.5);
H=(1.75,.75);
I=(2,0);
w=(A+I)/2;
x=(B+H)/2;
y=(C+G)/2;
z=(D+F)/2;
draw(ellipse(w, 1, .25));
draw(ellipse(x, .75, .1875));
draw(ellipse(y, .5, .125));
