for(int i = -11; i < 6; ++i)
{

draw((i,.5)--(i,-.5));
}

for(int i = -5; i < 6; ++i)
{

draw((.5,i)--(-.5,i));
}

dot((3,3));
dot((-3,-3));
dot((-9,-3));
dot((-3,3));
dot((0,0));
dot((-6,0));

draw((3,3)--(-3,-3)--(-9,-3)--(-3,3)--cycle, linewidth(.65));
