  return (sqrt(4*x));
}

real lowerparab (real x) {
  return (-sqrt(4*x));
}

pair A, B, C;

C = (6,0);
A = C + sqrt(5)*dir(140);
B = (5,upperparab(5));

draw(Circle(C,sqrt(5)));
draw(graph(upperparab,0,8));
draw(graph(lowerparab,0,8));
draw(A--B--C--cycle);

dot("$A$", A, NW);
dot("$B$", B, N);
dot("$C$", C, S);
